mapper.sql:
		FROM
			code a
			LEFT JOIN codegroup b ON b.cdgSeq = a.codeGroup_cdgSeq
		WHERE 1=1



        AND ifcdDelNy = #{shDelNy}



        	AND a.codeGroup_cdgSeq = #{shValue} 
            AND b.ifcgName LIKE CONCAT('%',#{shValue},'%')

            AND a.cdSeq = #{shValue}
            AND a.ifcdName LIKE CONCAT('%',#{shValue},'%')

mapper.insert:
	INSERT INTO code(
		ifcdName,
	    ifcdDelNy,
	    codeGroup_cdgSeq

	) value (
		#{ifcdName},
	    0,
	    #{codeGroup_cdgSeq}

	)


    SELECT last_insert_id()
