mapper.select:
SELECT a.fiSeq, a.futsalName, a.futsalPrice, a.futsalRegion, a.futsalAddress, a.futsalPhoneNumbar, a.futsalBusinessHours, a.futsalFacilityUsageRules, a.futsalCancellationPolicy, a.fiDelMy, a.regDateTime, a.modDateTime, a.zipCode, a.detailedAddress, a.latitude, a.hardness, region.cdSeq AS regionCdSeq, region.ifcdName AS regionName, COALESCE(review.avgStarScore, 0) AS avgStarScore, COALESCE(review.reviewCount, 0) AS reviewCount, (SELECT path FROM uploaded WHERE 1=1 AND pseq = fiSeq AND delNy = 0 ) AS path
FROM futsalinformation a LEFT JOIN code region ON a.futsalRegion = region.cdSeq LEFT JOIN ( SELECT futsalInformation_fiSeq, ROUND(AVG(starScore), 1) AS avgStarScore, COUNT(*) AS reviewCount FROM review GROUP BY futsalInformation_fiSeq ) review ON a.fiSeq = review.futsalInformation_fiSeq WHERE 1=1 AND a.fiDelMy = #{shDelNy}
AND a.futsalName LIKE CONCAT('%',#{shValue},'%') AND a.futsalAddress LIKE CONCAT('%',#{shValue},'%') AND a.futsalPrice LIKE CONCAT('%',#{shValue},'%')
AND a.futsalRegion = ( CASE WHEN #{shValue} = '서울' THEN 8 WHEN #{shValue} = '경기' THEN 9 ELSE -1 END )
AND a.regDateTime BETWEEN #{shDateStart} AND #{shDateEnd} AND a.modDateTime BETWEEN #{shDateStart} AND #{shDateEnd}
AND fiDelMy = 0
limit #{rowNumToShow} offset #{startRnumForMysql}
