mapper.select:
SELECT a.fiSeq, a.futsalName, a.futsalPrice, a.futsalRegion, a.futsalAddress, a.futsalPhoneNumbar, a.futsalBusinessHours, a.futsalFacilityUsageRules, a.futsalCancellationPolicy, a.fiDelMy, region.cdSeq AS regionCdSeq, region.ifcdName AS regionName
FROM futsalinformation a LEFT JOIN code region ON a.futsalRegion = region.cdSeq WHERE 1=1 AND a.fiDelMy = #{shDelNy}
AND a.futsalName LIKE CONCAT('%', #{shValue}, '%')
AND a.futsalName LIKE CONCAT('%',#{shValue},'%') AND a.futsalAddress LIKE CONCAT('%',#{shValue},'%') AND a.futsalPrice LIKE CONCAT('%',#{shValue},'%')
limit #{rowNumToShow} offset #{startRnumForMysql}

mapper.insert:
INSERT INTO futsalinformation( futsalName, futsalPrice, futsalRegion, futsalAddress, futsalPhoneNumbar, futsalBusinessHours, futsalFacilityUsageRules, futsalCancellationPolicy, fiDelMy
) value ( #{futsalName}, #{futsalPrice}, #{futsalRegion}, #{futsalAddress}, #{futsalPhoneNumbar}, #{futsalBusinessHours}, #{futsalFacilityUsageRules}, #{futsalCancellationPolicy}, 0 )
SELECT last_insert_id()
